 <div id="webcam" class="grid_16">        <table><tr><td valign=top>	<!-- include the JPEGCam JavaScript Library -->	<script type="text/javascript" src="/js/webcam/webcam.js"></script>        <!-- Start Geolocation Attempt -->        <script type="text/javascript">        var t = new bwTable();        var geo;        function getGeoLocation() {            try {                return navigator.geolocation;            } catch(e) {                return undefined;            }        }        function show_coords(position) {            var lat = position.coords.latitude;            var lon = position.coords.longitude;            element('lat').innerHTML = lat;            element('lon').innerHTML = lon;            t.updateRow(0, [ lat.toString(), lon.toString() ] );            dispResults();            //now_go();        }        function dispResults() {            element('results').innerHTML = t.getTableHTML();        }        function init() {            if((geo = getGeoLocation())) {                statusMessage('Using HTML5 Geolocation')                t.setHeader( [ 'Latitude', 'Longitude' ] );                t.addRow( [ '&nbsp;', '&nbsp;' ] );            } else {                statusMessage('HTML5 Geolocation is not supported.')            }            //geo.getCurrentPosition(show_coords, now_go());            geo.getCurrentPosition(show_coords);        }        window.onload = function() {            init();        }    </script>    <!-- End Geolocation attempt -->	<!-- write the movie to the page at 640x480 -->	<script language="JavaScript">		document.write('<div id="myWebcam" class="grid_9" >' + webcam.get_html(640, 480) + '</div>' );	</script>	<div id="upload_results" class="grid_4 push_1" ></div>	<div class="clear"></div>	<div id="counter" class="grid_1 push_5"></div>	<!-- Some buttons for controlling things -->	<br/>	<div class="grid_9 push_2">	<form>		<input type=button value="Configure..." onClick="webcam.configure()">		&nbsp;&nbsp;		<input type=button value="Take Snapshot" onClick="take_snapshot()">                &nbsp;&nbsp;                <input type=button value="Self-Timer" onClick="countDown()">    </form>        </div>        </div>	<!-- Code to handle the server response (see test.php) -->	<script language="JavaScript">		webcam.set_hook( 'onComplete', 'my_completion_handler' );		function take_snapshot() {                  var lat = document.getElementById('lat').innerHTML;                  if(lat == ""){                      var lat = 'shit';                  }                  var lon = document.getElementById('lon').innerHTML;                  if(lon == ""){                      var lon = 'shit';                  }                  var comment_raw = document.getElementById('comment').value;                  var comment = escape(comment_raw);                  webcam.set_api_url( 'webcam/test/' + lat + '/' + lon +'/'+comment);                  webcam.set_quality( 90 ); // JPEG quality (1 - 100)                  webcam.set_shutter_sound( true, 'http://stuff.chouinard.me/js/webcam/shutter.mp3' ); // play shutter click sound		  // take snapshot and upload to server	       	  document.getElementById('upload_results').innerHTML = '<h1>Uploading...</h1>';		  webcam.snap();		}		function my_completion_handler(msg) {			// extract URL out of PHP output			if (msg.match(/(http\:\/\/\S+)/)) {				var image_url = RegExp.$1;				// show JPEG image in page				document.getElementById('upload_results').innerHTML =					'<h1>Uploaded Image</h1>' + "<table><tr><td>" +					'<img src="' + image_url + '"></td></tr></table>';				// reset camera for another shot				webcam.reset();			}			else alert("PHP Error: " + msg);		}	</script></div>        <div id="results" class="grid_5"></div>